import { createVuetify } from 'vuetify';

export default createVuetify({
    theme: {
        dark: false,
        themes: {
          light: {
            primary: '#1976D2',
            secondary: '#424242',
            accent: '#82B1FF',
            error: '#FF5252',
            info: '#2196F3',
            success: '#4CAF50',
            warning: '#FFC107',
          },
        },
      },
    icons: {
        iconfont: 'mdi', // Material Design Icons
    },
    breakpoint: {
        mobileBreakpoint: 'sm', // Set the mobile breakpoint to small
    },
    lang: {
        current: 'en', // Set the default language
    },
    rtl: false, // Enable right-to-left text direction
    framework: {
        cssIconFont: 'mdi', // Material Design Icons
    },
    treeShake: true, // Remove un
});
